import React, { Component } from 'react';
import { connect } from 'react-redux';
import {  bindActionCreators } from 'redux';
import { fetchPingData } from '../../store/actions/projectActions';
import { fetchAP } from '../../store/actions/chartActions';
import { DataHeader } from './DataHeader';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';

/**
 * Specific details for each Pi Project
 */
class AllData extends Component {
  constructor(props) {
    super(props);
    this.state = {
      success: false,
      data: [],
      date: [new Date('2019-01-01Z00:00:00:000'), new Date('2021-01-01Z00:00:00:000')],
      myAP: []
    }
  }

  onChange = date => this.setState({ date })

  componentDidMount() {
    this.props.fetchPingData();
    this.props.fetchAP();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.data === undefined && this.props.data !== undefined) {
      this.setState({
        ...this.state,
        data: this.props.data
      })
    }
    if (prevProps.myAP === undefined && this.props.myAP !== undefined) {
      this.setState({
        ...this.state,
        myAP: this.props.myAP
      })
    }
  }

  render() {
    console.log("myAP", this.props.myAP)
    const { id } = this.props.match.params;
    const ind = parseInt(id) - 1;
    return (
      <div className="row">
        <div className="center-align grey darken-1">
             <DateRangePicker className="card-action grey darken-1"
              onChange={this.onChange}
              value={this.state.date}/>
            <DataHeader
            dataEntries={this.props.data} datePicker={this.state.date}/>
        </div>
      </div>
    )
  }
}

const matchStateToProps = (globalState) => {
  console.log("GLOBAL", globalState);
  return {
    projects: globalState.project.projects,
    data:  globalState.project.data,
    myAP: globalState.myAP
  }
}

const mapDispatchToProps = dispatch => 
bindActionCreators({
  fetchPingData: fetchPingData,
  fetchAP: fetchAP
}, dispatch)

export default connect(matchStateToProps, mapDispatchToProps)(AllData);
