const valid = {
  username: 'admin',
  password: 'password',
  success: false
}

const initAuth = {
  username: '',
  password: '',
  success: false,
}

const initBar = {
  success: false
}

const initLink = {
  success: true
}

const authReducer = (state = initAuth, action) => {
  //async actions can happen here
  if (action === undefined || action === null || action.result === undefined || action.result === null) {
    return state;
  }
  if (action.type === 'USER_VERIFICATION' && 
  (action.result.username === valid.username && 
    action.result.password === valid.password)) {
    action.result.success = true;
    initBar.success = true;
    return {
      ...state,
      success: action.result.success
    }
  } else {
    return initAuth;
  }
}

const navBarReducer = (state = initBar, action) => {
  if (action === undefined || action === null) {
    return state;
  }
  return initBar;
}

const signOutLinkReducer = (state = initLink, action) => {
  if (action === undefined || action === null) {
    return state;
  }
  if (action.type === "LINK_OUT") {
    initBar.success = false;
    return {
      ...state,
      success: action.result.success
    }
  } 
  return state;
}

export { authReducer, navBarReducer, signOutLinkReducer };
