export const addProject = (project) => {
  return (dispatch, getState) => {
    // make async call to database here
    dispatch({type:'ADD_PROJECT', project: project });
  }
};

export const fetchPingData = () => {
  return (dispatch, getState) => {
    fetch('http://localhost:5000/api/get_ping_info')
    .catch(err => {
      console.log(err)
      dispatch({
        type:"FETCH_ERROR",
        error: err,
        success: false
      })
    })
    .then(res => {
      return res.json()
    })
    .then(res => {
      if (res.error) {
        throw(res.error)
      }
      dispatch({
        type: "FETCH_DATA",
        data: res,
        success: true
      })
      return res;
    })
  }
}
