/**
 * using link or nav link to optimizing routing
 * NavLink is good to apply different styles to the active class
 * higher order component can be added to give it more functionality
 */

import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import SignedInLinks from './SignedInLinks';
import SignedOutLinks from './SignedOutLinks';


class NavBar extends Component {  
  constructor(props) {
    super(props);
    this.state = {
      success: false
    }
  }

  componentDidUpdate() {
    if (this.props.success !== this.state.success) {
      this.setState({
        ...this.state,
        success: this.props.success
      })
    }
  }

  render() {
    return (
      <nav className="nav-wrapper grey darken-3">
        <div className="container">
          <Link to="/" className="brand-logo">Project Dashboard</Link>
          { this.state.success ? (<SignedInLinks/>) : (<SignedOutLinks/>)}
        </div>
      </nav>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    success: state.navbar.success
  }
}



export default connect(mapStateToProps)(NavBar);
