/**
 * have to be class based to store user input field.  
 */
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addProject } from '../../store/actions/projectActions';


const axios = require('axios');

const initState = async () => {
  try {
    let response = await fetch('http://localhost:5000/api/get_rpi_info');
    let responseJson = await response.json();
    return resolve(responseJson[0].hostName);
   } catch(error) {
    console.error(error);
  }
}

class AddAP extends Component {
  state = {
    AP_name: '',
    Eth_Mac: '',
    Base_Radio_Mac: '',
    showResult: false
  }

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
    if (this.state.showResult) this.setState({showResult: false});
  }
  handleSubmit_add = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/add_ap', {
      AP_name: this.state.AP_name,
      Eth_Mac: this.state.Eth_Mac,
      Base_Radio_Mac: this.state.Base_Radio_Mac
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error.response);
    });
    this.setState({showResult: true});
  }

  handleSubmit_dlt = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/dlt_ap', {
      AP_name: this.state.AP_name,
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error.response);
    });
    this.setState({showResult: true});
    console.log(initState());
  }

  render() {
    return (
      <div className='container'>
        <form onSubmit={this.handleSubmit_add} className="white">
            <h5 className="grey-text text-darken-3">Add new Access Point</h5>
            <div className="input-field">
              <label htmlFor="AP_name">Access point name</label>
              <input type="text" id="AP_name" onChange={this.handleChange}/>
            </div>
            <div className="input-field">
              <label htmlFor="Eth_Mac">Ethernet Mac Address</label>
              <input type="text" id="Eth_Mac" onChange={this.handleChange}/>
            </div>
            <div className="input-field">
              <label htmlFor="Base_Radio_Mac">Base Radio Mac Address</label>
              <input type="text" id="Base_Radio_Mac" onChange={this.handleChange}/>
            </div>
            <div className="input-field">
              <button className="btn blue lighten-1 z-depth-0">Add</button>
              {this.state.showResult && <p>Submitted to database: {this.state.AP_name}</p>}
            </div>
        </form>

      <form onSubmit={this.handleSubmit_dlt} className="white">
          <h5 className="grey-text text-darken-3">Delete Access Point</h5>
          <div className="input-field">
            <label htmlFor="AP_name">Access point name</label>
            <input type="text" id="AP_name" onChange={this.handleChange}/>
          </div>
          <div className="input-field">
            <button className="btn blue lighten-1 z-depth-0">Delete</button>
            {this.state.showResult && <p>Deleted from database: {this.state.AP_name}</p>}
          </div>
      </form>
    </div>
    )
  }
}


//Async call to firebase database, NOT USED
const mapDispatchToProps = (dispatch) => {
  return {
    addProject: (project) => dispatch(addProject(project))
  }
}

export default connect(null, mapDispatchToProps)(AddAP);
