export const fetchJson = function(response){
  return {
    type: "RECEIVE_FAVORITES",
    response
  }
}
export const projectReducer = (state = initState, action) =>{
  return function(dispatch){
    console.log('send request')
    return fetch('http://localhost:5000/api/get_rpi_info')
      .then(response => response.json())
      .then(response => {
        dispatch(fetchJson(response))
      })
      .catch(error => {
        console.log(error)
      })
  }
}  

export default {
  projectReducer
}
