/**
 * have to be class based to store user input field.  
 */
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addProject } from '../../store/actions/projectActions';


const axios = require('axios');

class AddProject extends Component {
  state = {
    title: '',
    content: '',
    formSuccess: false,
    formError: false
  }

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/new_rpi', {
      hostName: this.state.content,
      realMac: this.state.title
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error.response);
    });
    if (error) {
      this.setState({formError: true, formSuccess: false});
    } else {
      this.setState({formError: false, formSuccess: true});
    }
  }

  render() {
    return (
      <div className='container'>
        <form success={this.state.formSuccess} error={this.state.formError} 
        onSubmit={this.handleSubmit} className="white">
          <Message
            success
            header="Form completed"
            content="Thank you for your contribution."
        />
        <Message
            error
            header="Missing fields!" 
            list={['All fields must be filled.']}
        />
            <h5 className="grey-text text-darken-3">Add a new Sensor</h5>
            <div className="input-field">
              <label htmlFor="title">MAC Address</label>
              <input type="text" id="title" onChange={this.handleChange}/>
            </div>
            <div className="input-field">
              <label htmlFor="content">Hostname</label>
              <textarea  id="content" className="materialize-textarea" onChange={this.handleChange}></textarea>
            </div>
            <div className="input-field">
              <button className="btn blue lighten-1 z-depth-0">Add</button>
            </div>
        </form>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    addProject: (project) => dispatch(addProject(project))
  }
}

export default connect(null, mapDispatchToProps)(AddProject);
