let initState = {
  projects: []
}

const testing = {
  projects: [
    {id:'1', title:"first input", content:"yes"},
    {id:'2', title:"second yes", content:"no"},
    {id:'3', title:"third", content:"maybe?"}
  ]
}
console.log(testing);
async function getData(url) {
  const response = await fetch(url);

  return response.json();
}

async function getJson() {
  const data = await getData('http://localhost:5000/api/get_rpi_info');
  initState.projects = data;
  console.log(initState);
}

const projectReducer =  (state = initState, action) => {
  getJson();
  return state;
}

export default projectReducer;
