const getJsonPromise = async () => {
  try {
    let response = await fetch('http://localhost:5000/api/get_rpi_info');
    let responseJson = await response.json();
    return responseJson;
   } catch(error) {
    console.error(error);
  }
}
const initState = {};
getJsonPromise().then( (value) => {
  initState = value;
});

const projectReducer = (state = initState, action) => {
  switch (action.type) {
    case 'ADD_PROJECT':
      console.log('created project', action.project);
      break;
    default: 
      break;
  }
  return state;
}

export default projectReducer;
