import React, { Component } from 'react';
import { connect } from 'react-redux';
import {  bindActionCreators } from 'redux';
import { fetchPingData } from '../../store/actions/projectActions';
import { fetchAP } from '../../store/actions/chartActions';
import { DataHeader } from './DataHeader';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';
import classNames from 'classnames';
import { lineAuthGraph }  from '../statistics/lineData';
import pieComponentMaker from '../statistics/pieData';
import barGraphMaker from '../statistics/barData';
import radarGraphMaker from '../statistics/radarData';
import { Link } from 'react-router-dom'
import { ChartEntry } from './ChartEntry';

/**
 * Specific details for each Pi Project
 */
class ProjectDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      success: false,
      data: [],
      date: [new Date('2019-01-01Z00:00:00:000'), new Date('3000-01-01Z00:00:00:000')],
      myAP: []
    }
  }

  onChange = date => this.setState({ date })

  componentDidMount() {
    this.props.fetchPingData();
    this.props.fetchAP();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.data === undefined && this.props.data !== undefined) {
      this.setState({
        ...this.state,
        data: this.props.data
      })
    }
    if (prevProps.myAP === undefined && this.props.myAP !== undefined) {
      this.setState({
        ...this.state,
        myAP: this.props.myAP
      })
    }
  }

  render() {
    console.log("myAP", this.props.myAP)
    const { id } = this.props.match.params;
    const ind = parseInt(id) - 1;
    const project = this.props.projects
    if (ind === 0) {
    return (
      <div className="row">
      <div className="center  project-details">
        <div className="card z-depth-0 white darken-1 ">
          <div className="card-content">
            <div>
              <span className={classNames({['card-title']: true, ['column']: true})}>{project[ind].title} - {id}</span>
              </div>               
              <div>
                <p>Access Points Detected: </p>
                <ChartEntry
                  chartEntries={this.props.myAP}/>
              </div>
            <p>{project[ind].content}</p>
            <p> Status: <span className="green-text text-darken-2"> Connected</span></p>
          </div>
        </div>
        <div className="valign-wrapper">
          {lineAuthGraph(this.props.data, "Authentication Response in Seconds")}
        </div>
        <div className="center-align">
          {pieComponentMaker(this.props.data)}
        </div>
        <div className="center-align">
          {barGraphMaker(this.props.data)}
        </div>
        <div className="center">
          {radarGraphMaker(this.props.data)}
        </div>
        </div>
             <div className="center-align grey darken-1">
             <DateRangePicker className="card-action grey darken-1"
              onChange={this.onChange}
              value={this.state.date}/>
            <DataHeader
            dataEntries={this.props.data} datePicker={this.state.date}/>
             </div> 
      </div>
    )
  } else {
      return (
        <div className="container section project-details grey darken-2">
          <div className="card z-depth-0">
            <div className="card-content">
              <span className="card-title">Invalid ID- {id} </span>
              <p>No Pis associated with this ID</p>
          </div>
        </div>
      </div>
      )
    }
  }
}

const matchStateToProps = (globalState) => {
  console.log("GLOBAL", globalState);
  return {
    projects: globalState.project.projects,
    data:  globalState.project.data,
    myAP: globalState.myAP
  }
}

const mapDispatchToProps = dispatch => 
bindActionCreators({
  fetchPingData: fetchPingData,
  fetchAP: fetchAP
}, dispatch)

export default connect(matchStateToProps, mapDispatchToProps)(ProjectDetails);
