import React from 'react';
import {Radar} from 'react-chartjs-2';

const radarData = (rawData = [], buttonState=[], apState=[]) => {
  let fiveHz = [], twoHz = [];
  rawData.forEach((val, index) => {
    if (val.RF_hz > 4000 && (buttonState[0] && val.AP_name === apState[0].AP_name) || (buttonState[1] && val.AP_name === apState[1].AP_name)) {
      fiveHz.push(val)
    } else if (val.RF_hz < 4000 && (buttonState[0] && val.AP_name === apState[0].AP_name) || (buttonState[1] && val.AP_name === apState[1].AP_name)) {
      twoHz.push(val)
    } 
  })
  let fiveData = fiveHz[0];
  let twoData = twoHz[0];
  console.log("FIVEDATA", fiveData)
  console.log("TWODATA", twoData)
  if (fiveData === undefined || twoData === undefined) {
    return {
      labels: ['test'],
      datasets: [
        {
          label: 'My First dataset',
          backgroundColor: 'rgba(179,181,198,0.2)',
          borderColor: 'rgba(179,181,198,1)',
          pointBackgroundColor: 'rgba(179,181,198,1)',
          pointBorderColor: '#fff',
          pointHoverBackgroundColor: '#fff',
          pointHoverBorderColor: 'rgba(179,181,198,1)',
          data: [65, 59, 90, 81, 56, 55, 40]
        },
        {
          label: 'My Second dataset',
          backgroundColor: 'rgba(255,99,132,0.2)',
          borderColor: 'rgba(255,99,132,1)',
          pointBackgroundColor: 'rgba(255,99,132,1)',
          pointBorderColor: '#fff',
          pointHoverBackgroundColor: '#fff',
          pointHoverBorderColor: 'rgba(255,99,132,1)',
          data: [28, 48, 40, 19, 96, 27, 100]
        }
      ]
    }
  }

  return {
    labels: ['Google Direct Res' ,'Google DNS Avg Res', 'RSSI', 'Signal Quality'],
    datasets: [
      {
        label: '5-GHz WiFi Band',
        backgroundColor: 'rgba(179,181,198,0.2)',
        borderColor: 'rgba(179,181,198,1)',
        pointBackgroundColor: 'rgba(179,181,198,1)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgba(179,181,198,1)',
        data: [fiveData.google_ping_ms, fiveData.DNS_ping_ms, fiveData.RSSI_dBm, fiveData.Signal_in_percent]
      },
      {
        label: '2-4GHz Wifi Band',
        backgroundColor: 'rgba(255,99,132,0.2)',
        borderColor: 'rgba(255,99,132,1)',
        pointBackgroundColor: 'rgba(255,99,132,1)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgba(255,99,132,1)',
        data: [twoData.google_ping_ms, twoData.DNS_ping_ms, twoData.RSSI_dBm, twoData.Signal_in_percent]
      }
    ]
  };
} 

export const radarGraphMaker = (rawData = [], buttonState=[], apState=[]) => {
  if (rawData === undefined) {
    return (
      <div></div>
    )
  }
  const data = radarData(rawData, buttonState, apState);
  return (
    <div>
      <p>Single comparison</p>
      <Radar data={data} 

      />
    </div>
  );
}

export default radarGraphMaker;
