import React, { Component } from 'react';
import { connect } from 'react-redux';
import {  bindActionCreators } from 'redux';
import { fetchPingData } from '../../store/actions/projectActions';
import { DataHeader } from './DataHeader';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';
import classNames from 'classnames';

/**
 * Specific details for each Pi Project
 */
class ProjectDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      success: false,
      data: [],
      date: [new Date('2019-01-01Z00:00:00:000'), new Date('3000-01-01Z00:00:00:000')]
    }
  }
  onChange = date => this.setState({ date })

  componentDidMount() {
    this.props.fetchPingData();
    console.log("PINGING")
  }

  componentDidUpdate(prevProps) {
    console.log(prevProps);
    console.log(this.props);
    if (prevProps.data === undefined && this.props.data !== undefined) {
      console.log("PLES")
      this.setState({
        ...this.state,
        data: this.props.data
      })
    }
  }

  render() {
    console.log("DATE:", this.state.date);
    console.log("DATA:", this.props.data);
    const { id } = this.props.match.params;
    const ind = parseInt(id);
    const project = this.props.projects
    if (ind === 0) {
    return (
      <div>
      <div className=" section project-details grey darken-2">
        <div className="card z-depth-0 grey darken-1">
          <div className="card-content">
            <div className="row">
              <span className={classNames({['card-title']: true, ['column']: true})}>{project[ind].title} - {id}</span>
              <span className={classNames({['card-title']: true, ['column']: true})}>Connected APs - {id}</span>
              </div>
            <p>{project[ind].content}</p>
          </div>
        </div>  
        <div className="card-action grey lighten-4 grey-text">
        <div style={{float : 'left', position: 'relative', left: '40%', margin:'-80px'}}>
        <DateRangePicker
        calendarAriaLabel="Toggle calendar"
        clearAriaLabel="Clear value"
        dayAriaLabel="Day"
        monthAriaLabel="Month"
        nativeInputAriaLabel="Date"
        onChange={this.onChange}
        yearAriaLabel="Year"
          onChange={this.onChange}
          value={this.state.date}
        />
      </div>
        </div>    
        </div>
        <div>
            <DataHeader dataEntries={this.props.data} datePicker={this.state.date}/>
        </div>
      </div>
    )
  } else {
      return (
        <div className="container section project-details grey darken-2">
          <div className="card z-depth-0">
            <div className="card-content">
              <span className="card-title">Invalid ID- {id} </span>
              <p>No Pis associated with this ID</p>
          </div>
        </div>
      </div>
      )
    }
  }
}

const matchStateToProps = (globalState) => {
  return {
    projects: globalState.project.projects,
    data:  globalState.project.data
  }
}

const mapDispatchToProps = dispatch => 
bindActionCreators({
  fetchPingData: fetchPingData
}, dispatch)

export default connect(matchStateToProps, mapDispatchToProps)(ProjectDetails);
