const getJsonPromise = (url) => {
  fetch(url)
    .then(response => {
      if (response.status !== 200) {
        console.log(
          "Looks like there was a problem. Status Code: " + response.status
        );
        return Promise.reject(//reject by returning a rejecting promise
          "Looks like there was a problem. Status Code: " + response.status
        );
      }

      // Examine the text in the response
      response.json().then(data => {
        console.log(data);
        return data;//still need to return the data here
      });
    })
    .catch(function (err) {
      console.log("Fetch Error :-S", err);
      //return the reject again so the caller knows something went wrong
      return Promise.reject(err);
    });
};
let initState = {};
getJsonPromise('http://localhost:5000/api/get_rpi_info').then( x => {
  initState = x;
});
console.log("test");
console.log("test", initState);
const projectReducer = (state = initState, action) => {
  switch (action.type) {
    case 'ADD_PROJECT':
      console.log('created project', action.project);
      break;
    default: 
      break;
  }
  return state;
}

export default projectReducer;
